array.string:
export PATH="/usr/local/bin:/opt/homebrew/bin:/usr/bin:/bin:/usr/sbin:/sbin";

if command -v terminal-notifier &> /dev/null; then
  terminal-notifier -title "Topgrade Update" \
    -message "System update started"
fi

{
  echo "===== Topgrade Started: $(date) =====";
  topgrade --yes --config "$HOME/.config/topgrade/topgrade.toml" || {
    echo "First attempt failed. Retrying in 30 seconds..."
    sleep 30
    topgrade --yes --config "$HOME/.config/topgrade/topgrade.toml"
  }
  echo "===== Topgrade Completed: $(date) =====";
} 2>&1

if command -v terminal-notifier &> /dev/null; then
  terminal-notifier -title "Topgrade Update" \
    -message "System update finished. Click to view logs" \
    -open "file://$HOME/topgrade_stdout.log"
fi
